import React,{Component} from "react";
import { useState, useEffect } from "react";
import { defaultMarker } from "./marker";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";

import axios from "axios";
import { map } from "leaflet";

const mapDataUrl = "http://localhost:3001/map"


export default function SimpleExample() {
      const [location, setLocation] = useState({lat:51.505,lng:-0.09});
      useEffect(()=>{
        const fetchData = async()=>{
          const res = await axios.get(mapDataUrl);
          console.log(location);
          setLocation({lat:res.data.lat, lng:res.data});
          console.log(location.lat);
        };
        fetchData();
      },[])

      // const position = [51.505, -0.09];
      const position = [51.505, -0.09];
      return (
        <MapContainer center={[location.lat, location.lng]} zoom={13} scrollWheelZoom={false}>
          <TileLayer
            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Marker position={position} icon={defaultMarker}>
            <Popup>
              A pretty CSS3 popup. <br /> Easily customizable.
            </Popup>
          </Marker>
        </MapContainer>
      )

  }
